userDefinedLinkHintCss:
/* Adapted from Giorgi Gzirishvili (#giogziro95)'s Arc Dark theme */

/* Linkhint boxes */
div > .vimiumHintMarker {
  background: #383c4a;
  font-size: 12px;
  border: 0.23em solid #383c4a;
  border-radius: 0.68em;
  box-shadow: 0em 0.1em 0.6em 0.1em rgba(0, 0, 0, 0.4);
}

/* Linkhint text */
div > .vimiumHintMarker span {
  color: #e2e3e7;
  font-size: inherit;
  text-shadow: none;
}

/* Linkhint matching characters */
div > .vimiumHintMarker > .matchingCharacter {
  color: #5294e2;
}
